%Modify Matrix Function
function [B] = modify_matrix(B)
    B = (B>=0).*B*4 + (B<0).*(B+3);
end

%Same function used in tutorial
function [A] = create_matrix(a,n,m)
    A = zeros(n,m);
    for i=1:n
        for j=1:m
            A(i,j)=a(i,j);
        end
    end
end
